INSERT INTO `buecher` (`id`, `katalog`, `nummer`, `kurztitle`, `kategorie`, `verkauft`, `kaufer`, `autor`, `title`, `sprache`, `foto`, `verfasser`, `zustand`) VALUES
(1, 11, 1, 'Aesop. Basel 1501', 10, 1, 14469, 'Aesop', 'Esopi appologi sive mythologi cum quibusdam carminum et fabularum additionibus Sebastiani Brant. 2 Tle. in 1 Bd. Mit 2 (1 blattgr.) Holzschnitt-Porträts u. 335 Textholzschnitten. Basel, J. Wolff aus Pforzheim, 1501. 2°. 203 (statt 204) nn. Bll. (Bl. s6 weiß). Schwarzer Maroquin-Band des 19. Jh. mit reicher Goldprägung.', ' ', 'book.jpg', 2, 'M'),
(2, 11, 2, 'Apian, Cosmographiae Introduct', 7, 1, 16067, 'Apian, Peter', 'Cosmographiae introductio: cum quibusdam Geometriae ac Astronomiae principiis ad eam rem necessariis. Mit Titelholzschnitt, ca. 25 (1 blattgr.) Textholzschnitten u. Holzschnitt-Druckerm. Venedig, F. Bindoni &amp; M. Pasini, 1537. 31 num., 1 nn. Bll. Mod. Prgt.', ' ', 'book.jpg', 2, 'M'),
(3, 11, 3, 'Bayerisches Landrecht 1616', 3, 1, 16116, ' ', 'Landrecht, Policey: Gerichts- Malefitz- und andere Ordnungen der Fürstenthumben Obern und Nidern Bayrn. 9 Tle. in 1 Bd. Mit gest. Titel, 8 großen Wappenholzschnitten sowie 11 Textholzschnitten auf 6 S. München, Heinrich, 1616. 2°. Blindgepr. Schweinsldr. d. Zt. über Holzdeckeln mit 2 Schließen (gebräunt u. fleckig, Rücken alt übermalt).', ' ', 'book.jpg', 1, 'S'),
(4, 11, 4, 'Beuther, Parainesis Dresde1587', 10, 0, 0, 'Beuther, A.', 'To theo mono he doxa. Parainesis Enkomiastike (graece). Gratulatio item ac votum ob natalem et pro auspicato in novum annum ingressu Friderici, filioli domini Friderici Guilelmi, Ducis Saxoniae. Mit Wappenholzschnitt. Dresden, Stöckel, 1587. 4°. 7 Bll. Ungebunden.', ' ', 'book.jpg', 4, 'M'),
(5, 11, 5, 'Mortier, Bilderbibel', 11, 1, 11568, 'Mortier, Pierre', 'Histoire du vieux et du nouveau Testament. 2 Tle. in 1 Bd. Mit 2 gest. Front., 2 wiederh. gest. Titelvign., 5 gefalt. Kupferktn., 214 Kupfertaf. mit je 2 Abbildungen (1 abgeschnitten) u. 34 gest. Vignetten. Antwerpen, Mortier, 1700. 2°. 8 Bll., 282 S., 4 Bll.; 13 Bll., 154, 20 S. Ldr. d. Zt. mit Rückenvergoldung u. dunkelrotem Rückenschild (Bezug spröde, 3 Ecken fachmännisch erneuert).', ' ', 'book.jpg', 5, 'G'),
(6, 11, 6, 'Biblia germanica (Basel 1729)', 9, 1, 10304, ' ', 'Biblia, Das ist: Die gantze Heilige Schrifft Alten und Neuen Testaments, Durch M. Luther verteutschet: Mit D. Pauli Tossani (...) Glossen und Auslegungen. Samt einer neuen Vorrede Herrn J. J. Ulrichs. Mit 1 gestoch. Titel, 15 (von 16) Kupfertafeln und 6 doppelblattgr. Karten. Basel, Thurneysen, 1729. 20 Bll., 48 S., 2 Bll., 680, 320, 104, 416 (von 422) S. Folio. Blindgepr. Schweinsleder über Holzdeckeln mit 4 Eckbeschlägen und 1 (mod.) Metallschließen (mit kl. Wurmspuren, 1 Kapital etwas eingeriessen, HDeckel mit kl. Fehlstellen, erneuerte Vorsätze).', ' ', 'book.jpg', 6, 'M'),
(7, 11, 7, 'Biblia Nürnberg 1736', 13, 1, 17624, ' ', 'Biblia. das ist: Die gantze Heilige Schrift, deß Alten und Neuen Testaments... Mit königl. Polnischen und ChurFürstl. Sächsis. allergnädigstem PRIVILEGIO. 2 Tl. in 1 Bd. Mit der Vorrede von Johann Michael Dilherrn. Mit 1 gestoch. Frontispiz, 18 Tafeln, 144 Textholzschnitten, einigen Textvignetten und zahlr. Initialien. Nürnberg, Endters Erben, 1736. Folio. Front., 48 Bll., 1181 S., 11 Bll. und Tafeln. Ldr. d. Zt. mit 8 Metalleckbeschlägen, mit erneuertem R. (Schließen fehlen, etwas berieben, Vorsätze neu).', ' ', 'book.jpg', 3, 'S'),
(8, 11, 8, 'Biblia germanica. Tl. 3. 1603', 8, 0, 0, ' ', 'Biblia, Mit der Außlegung. Der dritte Theil (von 3). Das ist: Die gantze heilige Schrifft dritten Theils ... Alles aus Herrn Lucae Osiandri Lateinischem Exemplar auffs trewlichst verdeutscht und an Tag geben. Durch David Förter. Stuttgart, Fürster, 1603. 2°. 6 Bll., 1030 S., 10 Bll. Blindgeprägter Schweinsldr. d. Zt. über Holzdeckeln (gedunkelt, Rückdeckelbezug etwas wurmstichig u. feuchtigkeitswellig, Rückenbezug unten eingerissen, Schließen fehlen).  ', ' ', 'book.jpg', 4, 'S'),
(9, 11, 9, 'Biblia graeco-latina', 14, 1, 14776, ' ', 'Novi Testamenti libri historici, graeci & latini, perpetuo commentario ex antiquitate, historiis, phililogia, illustrati... adornante Balduino Walaeo... praemissa est H. Stephani. Mit Kupfertitel und Holzschnittvignette auf dem typographischen Titel. Amsterdam, Ravenstein 1662. Gr.-4°. 9 Bll. 1304 S., 13 Bll. Pgt d. Zt. (beschabt, kratzspurig und braunfleckig).', ' ', 'book.jpg', 1, 'M'),
(10, 11, 10, 'Biblia latina. Plantin 1583', 5, 1, 11009, ' ', 'Biblia sacra quid in hac editione a theologis Lovaniensibus praestitum sit eorum praefatio indicat. 3 Tle. in 1 Bd. Mit gest. Titel, 2 (statt 3) doppelblattgr. Kupferktn., 2 doppelblattgr. Kupfertaf. u. 89 (7 blattgr.) Textkupfern. Antwerpen, Plantin, 1583. 2°. 480 (o.d.l.w.); 120 nn. Bll.; 117 S. Ldr. d. 18. Jh. mit Rückenvergoldung u. Rückenschild, beide Deckel mit goldgeprägtem Wappensupralibros (berieben u. fleckig, Gelenke gebrochen, Vorderdeckel fast lose, Rückenbezug an Kopf u. Fuß mit Fehlstellen).', ' ', 'book.jpg', 1, 'M'),
(11, 11, 11, 'Basnage, J. 1715', 10, 1, 11525, 'Basnage, J', 'T Groot waerelds tafereel, waar in de heilige en waereldsche geschiedenissen en veranderingen zedert de scheppinge des waerelds tot het uiteinde van de Openbaaring van Johannes, worden afgemaalt ... Wyders vertaalt, en met vaarzen verrykt door A. Alewyn. 3 Tle. in 1 Band. Mit 2 (1 kolor. und goldgehöht) gest. Titeln, 6 (davon 2 kol.) gest. Vignetten, 2 Porträts, 2 doppelblattgr. Karten und 86 (1 doppelblattgr.) gest. Tafeln. Amsterdam, Lindenberg 1715. Folio. 42 x 28 cm. 4 Bll., 12, 168, 92, 66 S. Kalbsldrbd. d. Zt., beide Deckel mit großem vergold. Mittelstück (berieben u. bestoßen, R. mit Fehlstellen).', ' ', 'book.jpg', 2, 'S'),
(12, 11, 12, 'Holl. Bilderbibel, 1721', 5, 0, 0, 'Basnage, Jacob', '''T groot Waerelds Tafereel, verbeeldende in Konst-Prenten de Heilige en Waereldsche Geschiedenissen, zedert den Aanvang des Waerelds tot het uiteinde van de Openbaring van Joannes. De Xde Druk. 2 Tle. in 1 Bd. Mit 2 (1 altkol.) Kupfertiteln, 2 gest. Porträts, 1 gest. Titelvign., 2 doppelblattgr. Kupferkarten, 1 (statt 2) doppelblattgr. Kupfertaf. u. 184 (4 auf dem Widmungsbl., davon 2 altkol.) etwa halbseitigen Textkupf. von R. de Hooghe inkl. Vign. von C. Luyken u. C Huybers. Amsterdam, Lindenberg, 1721. 2°. 5 Bll., 482 (recte 487) S. Prgt. um 1920 unter Verwendung alten Manuskriptpergaments (1 Gelenk mit Einriß).', ' ', 'book.jpg', 4, 'G'),
(13, 11, 13, 'Biblia Neerlandica - 1719', 10, 1, 14600, ' ', 'Biblia, dat is de gantsche H. Schriture, verfattende alle de Canonijcke Boecken des Ouden en des Nieuwen Testaments. 3 Tle. in 1 Bd. Mit 6 (dpblgr.) Karten u. Plänen und 25 Kupfern mit jeweils 6 Darstellungen. Dordrecht, Keur, 1719-20. Folio. Prachtvoller blindgepräg.  Ldr. d. Zt. mit Eckbeschlägen und Schließen (leicht berieben, Gelenke etwas eingerissen).', ' ', 'book.jpg', 2, 'G'),
(14, 11, 14, 'Borromeo, Constitutiones 1566', 5, 1, 17611, 'Borromeo, C.', 'Constitutiones et decreta condita in provinciali synodo Mediolanensi sub Carolo Borrhomaeo, archiepiscopo Mediolani. Mit Wappenholzschn. auf dem Titel u. 1 Textholzschnitt. Mailand, Baptista u.a., 1566. 12°. 11 Bll., 1 w. B., 268 S., 58 Bll. Prgt. d. Zt. (etwas knitterig u. fleckig).', ' ', 'book.jpg', 3, 'S'),
(15, 11, 15, 'Bouchet, Cassadorus Theophrast', 10, 0, 0, 'Bouchet, Jean', 'Eines alten Tugendhafften Teutschen Rechtmäßiger Eifer Uber die Losen Füchse dieser Welt: Unter derer seltzamen Figuren und Gesichten alle sündliche Laster der Menschen in allen Ständen deutlich abgemahlet und fürgestellet werden. Abermal aufs neue aufgeleget und übersehen. Mit 10 Textholzschnitten. O.O., 1681. 224 S. Pgt. d. Zt. mit handgeschr. RTitel (fleckig und etwas beschabt).', ' ', 'book.jpg', 3, 'G'),
(16, 11, 16, 'Buchanan, Franciscanus 1568', 7, 0, 0, 'Buchanan, George', 'Franciscanus et fratres, quibus accessere varia eiusdem & aliorum poemata quorum & titulos & nomina XVI. indicabit pagina. Eiusdem psalmos seorsim non sine accessione excudit. 3 Tle. in 1 Bd. Basel, Th. Guarinus, (1568). 8 Bll., 319, 176, 143 S. Pgt. d. 19. Jh. (etwas berieben und bestoßen, R. mit neuerem mont. Schild, Ex-Libris auf Innendeckel u. Vorsatz).', ' ', 'book.jpg', 4, 'G'),
(17, 11, 17, 'Bulla absolutionis 1517', 4, 1, 12633, ' ', 'Bulla absolutionis concilii Lateranen(si). Cum decreto expeditionis in Turchos. Rom, Guillery, 1517. 4 Bll. Pp.', ' ', 'book.jpg', 1, 'M'),
(18, 11, 18, 'Calderino, Dictionarium 1586', 12, 1, 11143, 'Calderino Mirani, C.', 'Dictionarium tum Latini, tum Italici sermonis studiosis, apprime congruens. Venedig, Valgrisi, 1586. 4°. 349 Bll., 1 w. Bl. Prgt. d. Zt. mit span. Kanten, Bindebandreste (knitterig, etwas fleckig, HDeckel mit 1 Fehlstelle).', ' ', 'book.jpg', 4, 'M'),
(19, 11, 19, 'Clement, del rois et ducs', 6, 1, 18625, 'Clement, Nicolas', 'Les Rois et Ducs d''Austrasie. Traduits en Francois par F. G. Dijonnois. Mit gestoch. TVignette und 63 gestoch. Porträt-Medaillons von Pierre Woeitiot. Köln, o. Dr., 1591. 8 Bll., 142 S. Ldr.-Bd. des 18. Jts. mit RVergoldung (etwas berieben u. bestoßen, VDeckel leicht aufgebogen).', ' ', 'book.jpg', 6, 'S'),
(20, 11, 20, 'Dell''Uva,Parte della Rime 1585', 9, 1, 17264, 'Dell''Uva, B.', 'Parte della Rime. Con bieve discorso dell''Epica Poesia. Florenz, Sermartelli, 1584 (in fine: 1585). 174 S., 1 Bl. Alter Prgt. mit neuerem RTitel (etwas fleckig, bestoßen, R. mit Läsuren).', ' ', 'book.jpg', 6, 'G'),
(21, 11, 21, 'Eder, Malleus haereticorum', 10, 0, 0, 'Eder, Georg', 'Malleus haereticorum. De variis falsorum dogmatum notis, atque censuris, libri duo. Ingolstadt, Sartorius, 1580. 40 Bll., 510 S., 1 Bl. Goldgeprägter Ldr. mit Monogramm "BK", Datierung "1665" u. Kreuzigungsszene auf dem Vorderdeckel, gepunzter Goldschnitt (gering berieben, Vergoldung oxidiert, Ecken professionell restauriert, Schließbänder u. vord. freier Vorsatz fehlen).', ' ', 'book.jpg', 3, 'G'),
(22, 11, 22, 'Eder, Triumphus 1558', 11, 0, 0, 'Eder, Georg', 'Triumphus D. Ferdinando I. Wien, Hofhalter, 1558. 4°. 76 Bll. Prgt. d. Zt. mit Bindebändern (etwas fleckig).', ' ', 'book.jpg', 2, 'M'),
(23, 11, 23, 'Gosky, Arbustum 1650', 8, 1, 20446, 'Gosky, Martin', 'Arbustum vel arboretum Augustaeum, AEternitati ac domui Augustae Selenianaae sacrum, satum autem &amp; educatum. Mit gest. Frontispiz, gest. Titel (beide verso mit Text), 40 (st. 41) Kupfertafeln (davon 26 (st.27) ganzseitig) und 5 gefalt. Kupfertafeln. Wolfenb&uuml;ttel, J. u. H. Stern, 1650. Folio. 72 nn. Bll., 588 Bll. Pgt. d. Zt. (etwas berieben u. fleckig).', ' ', 'book.jpg', 6, 'M'),
(24, 11, 24, 'Gebetbuch 1669', 9, 0, 0, ' ', 'Gottgeheiligtes ...  Das ist: Ein schön Geistreiches ... Gebet-Buch ... und schönen Kupfern gezieret durch Christian Holtzhammern. Mit dppblgr. Frontispiz u. 2 gestoch. Tafeln. Nürnberg, Felssecker, 1669. 12 Bll. Vorreden u. Widmungen, 625 S., 10 nn. S., 1 S. 12°. Ldr. d. Zt. (dat. 1699) mit 2 Schliessen und randgepunztem Goldschnitt (etwas berieben).', ' ', 'book.jpg', 5, 'G'),
(25, 11, 25, 'Historia de Vita Lutheri 1540', 5, 1, 18813, ' ', 'Historia de vita et actis Martini Lutheri. Mit kl. Holzschnitt-Porträt (Luther im Rund) auf dem Titel und einigen Holzschnitt-Initialien. Wittenberg, Lufft, 1549. 48 Bll. Kart. (VDeckel lose, fleckig).', ' ', 'book.jpg', 6, 'M'),
(26, 11, 26, '5 Stundenbuchbll. Kalendarium', 10, 0, 0, ' ', 'Konvolut von 5 beidseitig beschriebenen Pergamentblättern des Kalendariums eines französ. Stundenbuches, um 1500.', ' ', 'book.jpg', 5, 'G'),
(27, 11, 27, 'Frantzösische History 1574', 7, 1, 11916, ' ', 'Frantzösische History. Allerlay Religion und Wälthändlen so sich under Konig Henrico II, Francisco II unnd Carolo IX inn Frankreich biß auff gegenwärtige zeyt erhörter unnd unerhörter weiß sich habend zugetragen. Jetzund newlich auß Lateinischer unnd Frantzösischer sprach in unsere gemeine Teutsche sprach verteutschet. O. O. u. Dr., 1574. Fol. 4 nn., CCLXII num., 5 nn. Bll. Grün übermalter flex. Prgt. d. Zt. (etwas fleckig u. berieben, Schließbänder fehlen, 1 Innengelenk etwas angebrochen).', ' ', 'book.jpg', 1, 'G'),
(28, 11, 28, 'Breydenbach, Peregrinatio', 3, 1, 14765, 'Breydenbach, Bernhard von', 'Peregrinatio in terram sanctam. Mit blattgr. Titelholzschnitt, 13 Textholzschnitten, 2 Holzschnitt-Initialen (davon 1 mit Wappen), 1 kl. Holzschnitt-Druckermarke u. 7 (5 mehrfach gefalt., 2 doppelblattgr.) Holzschnitt-Tafeln, diese teilw. mit Text verso, die Jersualem-Ansicht mit 2 weiteren Holzschnitten verso. Mainz, E. Reuwich (mit den Typen P. Schöffers), 11. Februar 1486. 2°. 133 nn. Bll. (ohne das letzte weiße Bl.). Dunkelroter Maroquinbd. um 1820 mit Gold- u. Blindprägung, Goldschnitt (gering berieben).', ' ', 'book.jpg', 1, 'S'),
(29, 11, 29, 'Columna, De regimine principum', 8, 1, 17262, 'Columna, Aegidius (Romanus)', 'De regimine principum (katalan.). Hrsg. Aleix de Barcelona. Barcelona, Nikolaus Spindeler für Johann Çacoma, 2.XI.1480. 4°. 319 (d.l.w.) Bll.(o. d. erste w.). Moderner marmorierter Ldr. von Angulo y Jimenez mit reicher Goldprägung, Schnitt neu gefärbt.', ' ', 'book.jpg', 2, 'S'),
(30, 11, 30, 'Conradus, Concordantiae 1485', 5, 1, 10394, 'Conradus von Halberstadt', 'Concordantiae bibliorum. Mit 1 eingemalten Initiale in Rot. Nürnberg, A. Koberger, 27. Juni 1485. 2°. 418 nn. Bll. (ohne das erste u. letzte weiße Bl.). Restaurierter Ldr. d. Zt. mit reicher Blindprägung (Vorsätze erneuert).', ' ', 'book.jpg', 4, 'M'),
(31, 11, 31, 'Horatius Flaccus, Opera', 14, 1, 11805, 'Horatius Flaccus, Quintus', 'Opera cum quibusdam annotationibus imaginibusque pulcherimis (ed. J. Locher). Mit Holzschnitt-Druckermarke u. 168 teils mehrfach wiederholten Textholzschnitten. Straßburg, J. Grüninger, 12. März 1498. 2°. Röm. Typ., 3 Kol. 12 nn., 207 römisch num. Bll. (ohne das letzte weiße Bl.). Schweinsldr. d. Zt. über Holzdeckeln (gebräunt u. fleckig, einige Wurmstiche, freie Vorsätze erneuert, Schließenreste).', ' ', 'book.jpg', 2, 'M'),
(32, 11, 32, 'Mastri, Disputationes', 12, 1, 17842, 'Mastri, Bartolomeo', 'Disputationes theologicae in primum (-quartum) librum sententiarum, quibus ab adversantibus, tum veterum, tum recentiorum iaculis, Scoti theologia vindicatur. Editio novissima. Venedig, Balleonius, 1698. 2°. Mit 4 wiederh. Titelholzschnitten. Unterschiedliche Prgt. d. Zt. (2 Bde. fleckig, bestoßen u. wurmstichig, bei 2 Bdn. Rücken mit Prgt. überklebt).', ' ', 'book.jpg', 5, 'G'),
(33, 11, 33, 'Melanchthon, Corpus doctrinae', 2, 0, 0, 'Melanchthon, Philipp', 'Corpus Doctrinae Christianae. Das ist gantze Summa der rechten waren Christlichen Lere des heiligen Evangelii (...). Zerbst, B. Schmid, 1588. 8 Bll., 775 S., 19 Bl. 4°. Pgt. d. Zt. mit gepunztem Goldschnitt (etwas fleckig).', ' ', 'book.jpg', 4, 'G'),
(34, 11, 34, 'Melanchthon, Philipp. Chronica', 4, 1, 11360, 'Melanchthon, Philipp', 'Neuwe vollkommene Chronica Philippi Melanthonis. Zeytbuch Und Warhafftige Beschreibung Was von anfang der Welt biß auff diß gegenwertige Jar ... in Geistlichen und Weltlichen sachen rath und anschlägen in fried und kriegßzeiten in der gantzen Welt ... sich zugetragen fürgenommen verhandelt und außgeführt ist worden auß Biblischen und Heidnischen Historien ... Anfenglichs vnterm Namen Johan Carionis auffs kuertzest verfast Nachmals durch Herrn Philippum Melanthonem vnd D. Casparum Peucerum auffs neuwe vbersehen ... auß dem Latein verdeutscht durch M. Eusebium Menium ... Mit einem ordentlichen neuwen Register. Frankfurt, Lechler 1566. Folio. 31,5 x 22 cm. 12 unnum. Bll., CX Bll., 4 unum. Bll., CXIIII Bll., 12 unnum. Bll., CLXXXVIII, 8 unnum. Bll. HPrgt. des 17. Jhdts. Deckenbezüge stärker beschabt.', ' ', 'book.jpg', 1, 'S'),
(35, 11, 35, 'Narratio de colloquio 1560', 11, 1, 14899, ' ', 'Narratio de colloquio imperatoris Turcici cum mercatore Germano, mira & tristis: ab alio Germano mercatore scripta ex urbe Constantinopoli, ad quendam amicum. O. O. 1560. 15 Bll., 1 w. Bl. Pp. (leicht berieben).', ' ', 'book.jpg', 3, 'S'),
(36, 11, 36, 'Plinius Sec., Hist. Naturalis', 14, 1, 19600, 'Plinius Secundus', 'Historiae naturalis libri XXXVII. Aptissimis figuris exculti ab Alexandro Benedicto Ve. physico emendationes redditi , Ill. Mit 37 Textholzschn. und 37 Holzschn.-Initialen. Venedig, A. de Zanis für M. Sessa, 1513. 12 nn. Bll., 220 num. Bll. (d.l.w.) Folio. HLdr. d. 18. Jh. mit RVerg. u. goldgepräg. RSchd.', ' ', 'book.jpg', 6, 'M'),
(37, 11, 37, 'Pulci, Ciriffo 1572', 8, 1, 10871, 'Pulci, Luigi', 'Ciriffo calvaneo. Insieme con le epistole composte dal Pulci. Nuovamente ristampate. Mit Druckermarke. Florenz, Giunta, 1572. Kl-4°. 2 Bll., 121 S., 1 Bl. Flex. Prgt. d. Zt. (etwas knitterig, spätere Vorsätze).', ' ', 'book.jpg', 3, 'G'),
(38, 11, 38, 'Rasch, Erdbidem Chronic', 13, 1, 17988, 'Rasch, Johann', 'Erdbidem Chronic Nach art eines Calenders, sambt einem kurtzen bericht unn Catalogo Autorum. Darin allerley Erdbiden und Erdklüfften, vor Christi Geburt 1569. und sovil deren biß auff diß 1591. Jar her beschrieben. Mit gr. Titelholzschn. München, A. Berg, (1591). Kl.-4°. 20 Bll. Alter Kart.-Bd. (fleckig u. mit Wurmspuren).', ' ', 'book.jpg', 6, 'M'),
(39, 11, 39, 'Spangenberg, Adelsspiegel', 13, 1, 14893, 'Spangenberg Cyriacus', 'Adels Spiegel. Historischer Ausführlicher Bericht: Was Adel sey und heisse, woger er komme, wie mancherley er sey, und was denselben ziere und erhalte, auch hingegen verstelle und schwäche. 2 Bde. Mit 2 Titelholzschnitten, 1 Holzschnitt-Druckermarke u. 38 Textholzschnitten. Schmalkalden, Schmuck, 1591-94. 12 nn., 462 num. Bll.; 6 nn., 490 num., 7 nn. Bll. Blindgepr. mod. Ldr.-Bde. im Stil d. Zt. mit je 2 Schliessen (leicht bestoßen).', ' ', 'book.jpg', 5, 'M'),
